let squareWidth = canvasWidth/8;

class Piece {
    constructor(x, y, type, color, img) {
        this.x = x;
        this.y = y;
        this.w = squareWidth;
        this.h = squareWidth;
        this.color = color;
        this.type = type;
        this.img = img;
        this.image = document.createElement("img");
        this.image.src = this.img;
        this.selected = false;
        this.targetPosition = {x: this.x, y: this.y};
        this.hasMoved = false;
        this.protected = false;
        this.sizeInterval = 0;
        this.previousX = this.x;
        this.previousY = this.y;
        this.lastDeleted = null;
        this.waitTime = 0;

        this.drawX = this.x*squareWidth - squareWidth/2;
        this.drawY = this.y*squareWidth - squareWidth/2;

        if (this.color == "black") {
            blackPieces.push(this);
        } else {
            whitePieces.push(this);
        }

        pieces.push(this);
    }

    move() {
        this.sizeInterval++;
        this.waitTime -= 1;

        if (this.selected) {
            if (this.checkPossibleMoves(this.targetPosition)) {
                this.hasMoved = true;
                this.previousX = this.x;
                this.previousY = this.y;
                this.x = this.targetPosition.x;
                this.y = this.targetPosition.y;
                

                if (this.x > 8){
                    this.x = 8;
                }
                
                if (this.y > 8){
                    this.y = 8;
                }

                for (let i = 0; i < pieces.length; i++) {
                    if (pieces[i].x == this.x && pieces[i].y == this.y && pieces[i] != this) {
                        this.lastDeleted = pieces[i];
                        pieces[i].deleteSelf();
                    }
                }

                for (let i = 0; i < pieces.length; i++) {
                    let piece = pieces[i];

                    if (piece.type == "king") {
                        kingMoving = true;
                        if (piece.color == "white") {
                            whiteCheck = isCheck(piece, {x: piece.x, y: piece.y});
                        }
                        if (piece.color == "black") {
                            blackCheck = isCheck(piece, {x: piece.x, y: piece.y});
                        }
                        console.log(blackCheck);
                        kingMoving = false;
                    }
                }

                if (this.color == "white") {
                    if (whiteCheck) {
                        this.x = this.previousX;
                        this.y = this.previousY;
                        
                        if (this.lastDeleted != null) {
                            pieces.push(this.lastDeleted);
                            this.lastDeleted = null;
                        }
                    } else {
                        this.selected = false;
                        selectedPiece = null;
                    }
                }

                if (this.color == "black") {
                    if (blackCheck) {
                        this.x = this.previousX;
                        this.y = this.previousY;

                        if (this.lastDeleted != null) {
                            pieces.push(this.lastDeleted);
                            this.lastDeleted = null;
                        }
                    } else {
                        this.selected = false;
                        selectedPiece = null;
                    }
                }


                this.targetPosition.x = this.x;
                this.targetPosition.y = this.y;

                if (this.x != this.previousX || this.y != this.previousY) {
                    if (turn == "white") {
                        turn = "black";
                    } else {
                        turn = "white";
                    }
                }

                moveToPosition(this, {x: this.targetPosition.x *squareWidth - squareWidth/2, y: this.targetPosition.y*squareWidth - squareWidth/2}, 200);
            }
        }
    }

    checkPossibleMoves(targetMove) {
        if (this.type == "pawn") {
            if (kingMoving) {
                return checkTakesPawn(this, targetMove);
            } else {
                return (checkMovesPawn(this, targetMove) || checkTakesPawn(this, targetMove));
            }
        }
        if (this.type == "knight") {
            return checkMovesKnight(this, targetMove);
        }
        if (this.type == "rook") {
            return checkMovesRook(this, targetMove);
        }
        if (this.type == "bishop") {
            return checkMovesBishop(this, targetMove);
        }
        if (this.type == "queen") {
            return checkMovesQueen(this, targetMove);
        }
        if (this.type == "king") {
            kingMoving = true;
            let canMove = checkMovesKing(this, targetMove);
            kingMoving = false;
            return canMove;
        }
    }

    deleteSelf() {
        pieces.splice(pieces.indexOf(this), 1);
    }

    draw() {
        ctx.beginPath();
        if (this.selected) {
            let width = squareWidth * (1 + (Math.sin(this.sizeInterval/25))*0.05);
            let x = this.x*squareWidth - squareWidth/2;
            let y = this.y*squareWidth - squareWidth/2;

            ctx.drawImage(this.image, x - (width/2), y - (width/2), width, width);

        } else {
            ctx.drawImage(this.image, this.drawX - squareWidth/2, this.drawY - squareWidth/2, squareWidth, squareWidth);
        }

        ctx.closePath();
    }
}

function drawBoard() {
    for (let i = 0; i < 8; i++) {
        for (let j = 0; j < 8; j++) {
            if (i % 2 == 0) {
                if (j % 2 != 0) {
                    ctx.beginPath();
                    ctx.fillStyle = "#5C4033";
                    ctx.rect(j*squareWidth, i*squareWidth, squareWidth, squareWidth);
                    ctx.fill();
                    ctx.closePath();
                }
            } else {
                if (j % 2 == 0) {
                    ctx.beginPath();
                    ctx.fillStyle = "#5C4033";
                    ctx.rect(j*squareWidth, i*squareWidth, squareWidth, squareWidth);
                    ctx.fill();
                    ctx.closePath();
                }
            }
        }
    }
}

drawBoard();

function moveToPosition(obj, targetPosition, duration) {
    const startTime = performance.now();
    const startX = obj.drawX;
    const startY = obj.drawY;
    const distanceX = targetPosition.x - startX;
    const distanceY = targetPosition.y - startY;

    function updatePosition(currentTime) {
        const elapsedTime = currentTime - startTime;
        const fraction = Math.min(elapsedTime / duration, 1); 
        obj.drawX = startX + fraction * distanceX;
        obj.drawY = startY + fraction * distanceY;

        if (elapsedTime < duration) {
            requestAnimationFrame(updatePosition);
        } else {
            if (typeof onAnimationComplete === 'function') {
                onAnimationComplete();
            }
        }
    }

    requestAnimationFrame(updatePosition);
}

function canCastle(king, isLong) {
    let rook = null;

    if (!king.hasMoved) {
        if (isLong) {  
            if (king.color == "white") {
                if (whiteCheck) {
                    return false;
                }

                rook = longRookWhite;
                
                if (rook.hasMoved) {
                    return false;
                }

                checkingCastle = true;   

                if (isCheck(king, {x: king.x-1, y: king.y}) || isCheck(king, {x: king.x-2, y: king.y})) {
                    checkingCastle = false;

                    return false;
                }

                if (checkMovesRook(rook, {x: king.x-1, y: king.y})) {
                    checkingCastle = false;

                    return true;
                }

                checkingCastle = false;

                return false;
            } else {
                rook = longRookBlack;
                if (blackCheck) {
                    return false;
                }
                
                if (rook.hasMoved) {
                    return false;
                }
                
                checkingCastle = true;   
                
                if (isCheck(king, {x: king.x-1, y: king.y}) || isCheck(king, {x: king.x-2, y: king.y})) {
                    checkingCastle = false;

                    return false;
                }

                if (checkMovesRook(rook, {x: king.x-1, y: king.y})) {
                    checkingCastle = false;
                    return true;
                }

                checkingCastle = false;

                return false;
            }
        } else if (!isLong) {  
            if (king.color == "white") {
                if (whiteCheck) {
                    return false;
                }

                rook = shortRookWhite;

                if (rook.hasMoved) {
                    return false;
                }

                checkingCastle = true;  


                if (isCheck(king, {x: king.x+1, y: king.y}) || isCheck(king, {x: king.x+2, y: king.y})) {
                    checkingCastle = false;

                    return false;
                }

                if (checkMovesRook(rook, {x: king.x+1, y: king.y})) {
                    checkingCastle = false;


                    return true;
                }

                
                //console.log(checkMovesRook(rook, {x: king.x+1, y: king.y}))

                checkingCastle = false;

                return false;
            } else {
                rook = shortRookBlack;

                if (blackCheck) {
                    return false;
                }
                if (rook.hasMoved) {
                    return false;
                }

                checkingCastle = true;   

                if (isCheck(king, {x: king.x+1, y: king.y}) || isCheck(king, {x: king.x+2, y: king.y})) {
                    checkingCastle = false;
                    return false;
                }
                
                if (checkMovesRook(rook, {x: king.x+1, y: king.y})) {
                    checkingCastle = false;
                    return true;
                }
                checkingCastle = false;

                return false;
            }

        }
    } else {
        return false;
    }

    return false;
}

function checkMovesPawn(pawn, target) {
    const targetPiece = getPieceAtPosition(target);

    if (targetPiece != "empty") {
        if (targetPiece.color == pawn.color) {
            return false;
        } 
    }

    if (pawn.color == "white") {
        if (target.y == pawn.y-2 && target.x == pawn.x && !pawn.hasMoved && targetPiece == "empty") {
            return true;
        }
        
        if (target.y == pawn.y-1) {
            if (pawn.x == target.x) {
                if (targetPiece == "empty") {
                    return true;
                } else {
                    return false;
                }
            }
        }
    } else if (pawn.color == "black") {
        if (target.y == pawn.y+2 && target.x == pawn.x && !pawn.hasMoved && targetPiece == "empty") {
            return true;
        }
        
        if (target.y == pawn.y+1) {
            if (pawn.x == target.x) {
                if (targetPiece == "empty") {
                    return true;
                } else {
                    return false;
                }
            }
        }
    }

    return false;
}

