function checkMovesRook(rook, target) {
    const protectedPieces = [];

    if (target.x !== rook.x && target.y !== rook.y) {
        return false;
    }

    if (checkingCastle) {
        if (getPieceAtPosition(target) != "empty") {
            return false;
        }
    }

    const isPathClear = (start, end, fixed, isHorizontal) => {
        const increment = start < end ? 1 : -1;
        for (let i = start + increment; i !== end; i += increment) {
            const pos = isHorizontal ? { x: i, y: fixed } : { x: fixed, y: i };
            const pieceAtPos = getPieceAtPosition(pos);
            if (pieceAtPos != "empty") {
                if (pieceAtPos.color == rook.color) {
                    protectedPieces.push(pieceAtPos);
                    pieceAtPos.protected = true;
                    return false;
                } else {
                    return false;
                }
            }
        }

        return true;
    };

    if (target.x !== rook.x) {
        if (!isPathClear(rook.x, target.x, rook.y, true)) {
            return false;
        }
    } else if (target.y !== rook.y) {
        if (!isPathClear(rook.y, target.y, rook.x, false)) {
            return false;
        }
    }

    const targetPiece = getPieceAtPosition(target);
    
    if (targetPiece != "empty") {
        if (targetPiece.color != rook.color) {
            if (targetPiece.type == "king") {
                return true;
            }
            //targetPiece.deleteSelf();
        } else {
            targetPiece.protected = true;
            return false;
        }
    }
    
    return true;
}

function checkMovesBishop(bishop, target) {
    const protectedPieces = [];

    let thisTargetPosition = {x: target.x, y: target.y};

    const targetPiece = getPieceAtPosition(thisTargetPosition);
    
    if (!(Math.abs(target.x - bishop.x) !== Math.abs(target.y - bishop.y))) {
        const isBishopPathClear = (startX, startY, endX, endY) => {
            const xIncrement = startX < endX ? 1 : -1;
            const yIncrement = startY < endY ? 1 : -1;
            let x = startX + xIncrement;
            let y = startY + yIncrement;
    
            let loopTimes = 0;
    
            while (x !== endX && y !== endY && loopTimes < 50) {
                const pos = { x: x, y: y };
                const pieceAtPos = getPieceAtPosition(pos);
                if (pieceAtPos != "empty") {
                    if (pieceAtPos.color == bishop.color) {
                        protectedPieces.push(pieceAtPos);
                        pieceAtPos.protected = true;
                        return false;
                    } else {
                        return false;
                    }
                }
                x += xIncrement;
                y += yIncrement;
                loopTimes++;
            }
    
            return true;
        };
    
        if (!isBishopPathClear(bishop.x, bishop.y, target.x, target.y)) {
            return false;
        }
        
        if (targetPiece != "empty") {
            if (targetPiece.color != bishop.color) {  
                if (targetPiece.type == "king" || checkingCastle) {
                    return true;
                } 
                //targetPiece.deleteSelf();
            } else {
                targetPiece.protected = true;
                return false;
            }
        }

        return true;
    }
    
    return false;
}

function checkMovesKing(king, target) {
    let thisTargetPosition = {x: target.x, y: target.y}

    if (target.x*squareWidth <= 0 || target.y*squareWidth <= 0 || target.x*squareWidth > 8 || target.y*squareWidth > 8) {
        return false;
    }

    const targetPiece = getPieceAtPosition(thisTargetPosition);

    if (targetPiece.type == "rook" && targetPiece.color == king.color) {
        if (king.color == "white") {
            if (targetPiece == shortRookWhite) {
                if (whiteCastle.short) {
                    targetPiece.targetPosition.x = 6;
                    targetPiece.targetPosition.y = king.y;
                    targetPiece.hasMoved = true;
                    targetPiece.previousX = targetPiece.x;
                    targetPiece.previousY = targetPiece.y;
                    targetPiece.x = targetPiece.targetPosition.x;
                    targetPiece.y = targetPiece.targetPosition.y;
                    king.targetPosition.x = target.x - 1;
                    moveToPosition(targetPiece, {x: targetPiece.targetPosition.x *squareWidth - squareWidth/2, y: targetPiece.targetPosition.y*squareWidth - squareWidth/2}, 200);
                    return true;
                }
            }
            if (targetPiece == longRookWhite) {
                if (whiteCastle.long) {
                    targetPiece.targetPosition.x = 4;
                    targetPiece.targetPosition.y = king.y;
                    targetPiece.hasMoved = true;
                    targetPiece.previousX = targetPiece.x;
                    targetPiece.previousY = targetPiece.y;
                    targetPiece.x = targetPiece.targetPosition.x;
                    targetPiece.y = targetPiece.targetPosition.y;
                    king.targetPosition.x = target.x + 2;
                    moveToPosition(targetPiece, {x: targetPiece.targetPosition.x *squareWidth - squareWidth/2, y: targetPiece.targetPosition.y*squareWidth - squareWidth/2}, 200);
                    return true;
                }
            }
        }

        if (king.color == "black") {
            if (targetPiece == shortRookBlack) {
                if (blackCastle.short) {
                    targetPiece.targetPosition.x = 6;
                    targetPiece.targetPosition.y = king.y;
                    targetPiece.hasMoved = true;
                    targetPiece.previousX = targetPiece.x;
                    targetPiece.previousY = targetPiece.y;
                    targetPiece.x = targetPiece.targetPosition.x;
                    targetPiece.y = targetPiece.targetPosition.y;
                    king.targetPosition.x = target.x - 1;
                    moveToPosition(targetPiece, {x: targetPiece.targetPosition.x *squareWidth - squareWidth/2, y: targetPiece.targetPosition.y*squareWidth - squareWidth/2}, 200);
                    return true;
                }
            }
            if (targetPiece == longRookBlack) {
                if (blackCastle.long) {
                    targetPiece.targetPosition.x = 4;
                    targetPiece.targetPosition.y = king.y;
                    targetPiece.hasMoved = true;
                    targetPiece.previousX = targetPiece.x;
                    targetPiece.previousY = targetPiece.y;
                    targetPiece.x = targetPiece.targetPosition.x;
                    targetPiece.y = targetPiece.targetPosition.y;
                    king.targetPosition.x = target.x + 2;
                    moveToPosition(targetPiece, {x: targetPiece.targetPosition.x *squareWidth - squareWidth/2, y: targetPiece.targetPosition.y*squareWidth - squareWidth/2}, 200);
                    return true;
                }
            }
        }
    }

    for (let i = 0; i < pieces.length; i++) {
        if (pieces[i].type == "king" && pieces[i].color != king.color && king.selected) {
            let otherKing = pieces[i];

            if (Math.abs(otherKing.x - target.x) <= 1 && Math.abs(otherKing.y - target.y) <= 1) {
                return false;
            }
        }
    }

    if (Math.abs(target.x - king.x) > 1 || Math.abs(target.y - king.y) > 1) {
        return false;
    }

    for (let i = 0; i < pieces.length; i++) {
        pieces[i].protected = false;
    }
    

    /*for (let i = 0; i < pieces.length; i++) {
        if (pieces[i].color != king.color && pieces[i].type != "king") {
            if (pieces[i].checkPossibleMoves(target)) {
                return false;
            }
        }
    }*/

    if (isCheck(king, target)) {
        if (targetPiece.x != target.x || targetPiece.y != target.y) {
            return false;
        }
    }

    if (targetPiece != "empty") {
        if (targetPiece.color != king.color) {
            if (targetPiece.protected) {
                return false;
            } else {
                if (!checkingCastle && canTake) {
                    //targetPiece.deleteSelf();
                }
            }
        } else {
            targetPiece.protected = true;
            return false;
        }
    }

    return true;
}

function canKingMoveAtAll(king) {
    if (checkMovesKing(king, {x: king.x + 1, y: king.y + 1})) {
        console.log("Position: " + king.x + 1 + ", " + king.y + 1);
        return true;
    }

    if (checkMovesKing(king, {x: king.x+1, y: king.y})) {
        console.log("Position: " + king.x + 1 + ", " + king.y);

        return true;
    }

    if (checkMovesKing(king, {x: king.x+1, y: king.y - 1})) {
        console.log("Position: " + king.x + 1 + ", " + king.y - 1);

        return true;
    }

    if (checkMovesKing(king, {x: king.x, y: king.y + 1})) {
        console.log("Position: " + king.x + ", " + king.y + 1);

        return true;
    }
    
    if (checkMovesKing(king, {x: king.x, y: king.y - 1})) {
        console.log("Position: " + king.x + ", " + king.y - 1);

        return true;
    }

    if (checkMovesKing(king, {x: king.x-1, y: king.y + 1})) {
        console.log("Position: " + king.x - 1 + ", " + king.y + 1);

        return true;
    }

    if (checkMovesKing(king, {x: king.x-1, y: king.y})) {
        console.log("Position: " + king.x - 1 + ", " + king.y);

        return true;
    }

    if (checkMovesKing(king, {x: king.x - 1, y: king.y - 1})) {
        console.log("Position: " + king.x - 1 + ", " + king.y - 1);

        return true;
    }

    return false;
}

function canBlock(king) {
    //console.log("Checking if can block for", king.color);
    for (let i = 0; i < pieces.length; i++) {
        let piece = pieces[i];
        if (king.color == piece.color) {
            if (piece.type == "pawn") {
                let moveY = piece.color === "black" ? piece.y + 1 : piece.y - 1;
                let captureMoves = [
                    {x: piece.x - 1, y: moveY},
                    {x: piece.x + 1, y: moveY}
                ];

                for (let move of captureMoves) {
                    let targetPiece = getPieceAtPosition(move);
                    if (targetPiece !== "empty" && targetPiece.color !== piece.color) {
                        let originalX = piece.x;
                        let originalY = piece.y;
                        let targetOriginalX = targetPiece.x;
                        let targetOriginalY = targetPiece.y;

                        piece.x = move.x;
                        piece.y = move.y;
                        targetPiece.x = -1; 
                        targetPiece.y = -1;

                        let stillInCheck = isCheck(king, {x: king.x, y: king.y});

                        piece.x = originalX;
                        piece.y = originalY;
                        targetPiece.x = targetOriginalX;
                        targetPiece.y = targetOriginalY;

                        if (!stillInCheck) {
                            return true;
                        }
                    }
                }
            }

            if (piece.type != "king") {
                //console.log("Checking moves for", piece.type);
                for (let x = 1; x < 9; x++) {
                    for (let y = 1; y < 9; y++) {
                        kingMoving = true;
                        
                        if (piece.checkPossibleMoves({x: x, y: y})) {
                            //console.log("Found possible move for", piece.type, "to", x, y);
                            
                            let originalX = piece.x;
                            let originalY = piece.y;
                            piece.x = x;
                            piece.y = y;
                            
                            let stillInCheck = isCheck(king, {x: king.x, y: king.y});
                            //console.log("After move, still in check?", stillInCheck);
                            
                            piece.x = originalX;
                            piece.y = originalY;
                            
                            if (!stillInCheck) {
                                kingMoving = false;
                                return true;
                            }
                        }
                    }
                }
            }
        }
    }
    kingMoving = false;
    return false;
}


function checkMovesQueen(queen, target) {
    const protectedPieces = [];

    let thisTargetPosition = {x: target.x, y: target.y};

    const targetPiece = getPieceAtPosition(thisTargetPosition);
    
    if (!(Math.abs(target.x - queen.x) !== Math.abs(target.y - queen.y))) {
        const isBishopPathClear = (startX, startY, endX, endY) => {
            const xIncrement = startX < endX ? 1 : -1;
            const yIncrement = startY < endY ? 1 : -1;
            let x = startX + xIncrement;
            let y = startY + yIncrement;
    
            let loopTimes = 0;
    
            while (x !== endX && y !== endY && loopTimes < 50) {
                const pos = { x: x, y: y };
                const pieceAtPos = getPieceAtPosition(pos);
                if (pieceAtPos != "empty") {
                    if (pieceAtPos.color == queen.color) {
                        protectedPieces.push(pieceAtPos);
                        pieceAtPos.protected = true;
                        return false;
                    } else {
                        return false;
                    }
                }
                x += xIncrement;
                y += yIncrement;
                loopTimes++;
            }
    
            return true;
        };
    
        if (!isBishopPathClear(queen.x, queen.y, target.x, target.y)) {
            return false;
        }
        
        if (targetPiece != "empty") {
            if (targetPiece.color != queen.color) {  
                if (targetPiece.type == "king" || checkingCastle) {
                    return true;
                } 
                //targetPiece.deleteSelf();
            } else {
                targetPiece.protected = true;
                return false;
            }
        }

        return true;
    } else {
        if (target.x !== queen.x && target.y !== queen.y) {
            return false;
        }
        
        const isPathClear = (start, end, fixed, isHorizontal) => {
            const increment = start < end ? 1 : -1;
                for (let i = start + increment; i !== end; i += increment) {
                const pos = isHorizontal ? { x: i, y: fixed } : { x: fixed, y: i };
                const pieceAtPos = getPieceAtPosition(pos);
                if (pieceAtPos != "empty") {
                    if (pieceAtPos.color == queen.color) {
                        protectedPieces.push(pieceAtPos);
                        pieceAtPos.protected = true;
                        return false;
                    } else {
                        return false;
                    }
                }
            }
        
            return true;
        };
        
        if (target.x !== queen.x) {
            if (!isPathClear(queen.x, target.x, queen.y, true)) {
                return false;
            }
        } else if (target.y !== queen.y) {
            if (!isPathClear(queen.y, target.y, queen.x, false)) {
                return false;
            }
        }
        
            
        if (targetPiece != "empty") {
            if (targetPiece.color != queen.color) {  
                if (targetPiece.type == "king" || checkingCastle) {
                    return true;
                } 
                //targetPiece.deleteSelf();
            } else {
                targetPiece.protected = true;
                return false;
            }
        }
        
        return true;
    }

}

function isCheck(king, target) {
    let originalSelected = kingMoving;
    kingMoving = true;

    let tempPieces = pieces.map(piece => ({
        piece: piece,
        x: piece.x,
        y: piece.y
    }));

    let kingOriginalX = king.x;
    let kingOriginalY = king.y;
    if (target.x !== king.x || target.y !== king.y) {
        king.x = target.x;
        king.y = target.y;
    }

    let inCheck = false;

    for (let i = 0; i < pieces.length; i++) {
        if (pieces[i].color != king.color && pieces[i].type != "king") {
            if (pieces[i].type == "pawn") {
                kingMoving = true;
                if (checkTakesPawn(pieces[i], target)) {
                    inCheck = true;
                    break;
                }
            } else if (pieces[i].checkPossibleMoves(target)) {
                inCheck = true;
                break;
            }
        }
    }

    tempPieces.forEach(temp => {
        temp.piece.x = temp.x;
        temp.piece.y = temp.y;
    });

    king.x = kingOriginalX;
    king.y = kingOriginalY;

    kingMoving = originalSelected;

    return inCheck;
}

function getPieceAtPosition(target) {
    for (let i = 0; i < pieces.length; i++) {
        if (target.x == pieces[i].x && target.y == pieces[i].y) {
            return pieces[i];
        }
    }

    return "empty";
}


function getPieceAtPosition(target) {
    for (let i = 0; i < pieces.length; i++) {
        if (target.x == pieces[i].x && target.y == pieces[i].y) {
            return pieces[i];
        }
    }

    return "empty";
}

