function mainLoop() {
    interval++;
    sizeInterval++;
    moveLoop();
    drawLoop();
    requestAnimationFrame(mainLoop);
    if (checkmateNow) {
        return;
    }
}

function moveLoop() {
    mouseX = Math.round(mouseX/(squareWidth/1))*(squareWidth/1);
    mouseY = Math.round(mouseY/(squareWidth/1))*(squareWidth/1);

    for (let i = 0; i < pieces.length; i++) {
        pieces[i].move();
    }

    for (let i = 0; i < pieces.length; i++) {
        let piece = pieces[i];
        
        kingMoving = true;
        if (piece.type == "king") {
            if (piece.color == "white") {
                whiteCastle = {short: canCastle(piece, false), long: canCastle(piece, true)}

                if (!canKingMoveAtAll(piece) && whiteCheck) {
                    if (!canBlock(piece)) {
                        checkmate("Black");
                    }
                }
            }
            if (piece.color == "black") {
                blackCastle = {short: canCastle(piece, false), long: canCastle(piece, true)}

                if (blackCheck) {
                    console.log(canKingMoveAtAll(piece)); 
                }

                if (!canKingMoveAtAll(piece) && blackCheck) {
                    if (!canBlock(piece)) {
                        checkmate("White");
                    }
                }
            }
        }
        kingMoving = false;
    }

    
}

function drawLoop() {
    ctx.clearRect(0, 0, 700, 700);

    drawBoard();

    for (let i = 0; i < pieces.length; i++) {
        pieces[i].draw();
    }

    ctx.beginPath();
    ctx.fillStyle = "rgba(255, 0, 0, 0.3)";
    ctx.rect(mouseX, mouseY, squareWidth, squareWidth);
    ctx.fill();
    ctx.closePath();
}

spawnPieces();

mainLoop();

